INSERT INTO pokemon_region (id, nombre, descripcion) VALUES
(1, 'Kanto', 'La región original donde comienzan las aventuras de Pokémon. Hogar de los primeros 151 Pokémon y de líderes de gimnasio legendarios.'),
(2, 'Johto', 'Una región conocida por su rica historia y conexión con Kanto. Es el hogar de los Pokémon legendarios Lugia y Ho-Oh.'),
(3, 'Hoenn', 'Una región tropical famosa por sus vastos océanos y áreas montañosas. Hogar de Kyogre, Groudon y Rayquaza.'),
(4, 'Sinnoh', 'Una región montañosa conocida por su historia mitológica y por ser hogar de Dialga, Palkia y Giratina.'),
(5, 'Unova', 'Una región moderna inspirada en ciudades metropolitanas. Hogar de Reshiram, Zekrom y Kyurem.'),
(6, 'Kalos', 'Una región inspirada en Francia, famosa por su diversidad cultural y Xerneas, Yveltal y Zygarde.'),
(7, 'Alola', 'Una región insular tropical conocida por sus formas regionales únicas y Tapu Koko y sus guardianes.'),
(8, 'Galar', 'Una región inspirada en Inglaterra, famosa por su Liga Pokémon y Eternatus.'),
(9, 'Paldea', 'Una región académica conocida por sus paisajes diversos y Pokémon especiales como Miraidon y Koraidon.');